    //    return () => {
    //        document.removeEventListener('keypress', listener)
    //    };
    //}, [currentShip])

    // TODO
    // space pressed
    // click


    return (
        <Field>
            {initScreen.tempField.getCells().map((row, i) => (
                <Row key={i}>
                    {row.map((type, j) => (
                        <Cell
                          type={type}
                          key={`${i}-${j}`}
                          onMouseOver={handleMouseOver(i, j)}
                          onClick={handleClick(i, j)}
                          />
                     ))}
                </Row>
            ))}
        </Field>
    );
};
